mapper.select:
SELECT * FROM news

    is_deleted = 0

        AND (
        title LIKE CONCAT('%', #{request.keyword}, '%') OR
        author LIKE CONCAT('%', #{request.keyword}, '%') OR
        summary LIKE CONCAT('%', #{request.keyword}, '%')
        )


        AND status = #{status}


        AND create_time >= #{request.startDate}


        AND create_time <= #{request.endDate}


ORDER BY create_time DESC

SELECT COUNT(*) FROM news

    is_deleted = 0

        AND status = #{status}

SELECT * FROM news

    is_deleted = 0

        AND status = #{status}


ORDER BY view_count DESC
LIMIT #{limit}

SELECT id, title, view_count, status, create_time, author
FROM news

    is_deleted = 0

        AND status = #{status}


ORDER BY view_count DESC
LIMIT #{limit}
